import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import products from '/static/data/products.json'


class SizeFilter extends Component {
  render() {
    return (
      <div>
      Sizes:
      <p>
          <input
            type="checkbox"
            checked={this.props.inStockOnly}
            onChange={this.handleInStockChange}
          />
          {' '}
          XS
          <input
            type="checkbox"
            checked={this.props.inStockOnly}
            onChange={this.handleInStockChange}
          />
          {' '}
          S
          <input
            type="checkbox"
            checked={this.props.inStockOnly}
            onChange={this.handleInStockChange}
          />
          {' '}
          M
          <input
            type="checkbox"
            checked={this.props.inStockOnly}
            onChange={this.handleInStockChange}
          />
          {' '}
          X
          <input
            type="checkbox"
            checked={this.props.inStockOnly}
            onChange={this.handleInStockChange}
          />
          {' '}
          ML
          <input
            type="checkbox"
            checked={this.props.inStockOnly}
            onChange={this.handleInStockChange}
          />
          {' '}
          L
          <input
            type="checkbox"
            checked={this.props.inStockOnly}
            onChange={this.handleInStockChange}
          />
          {' '}
          XL
          <input
            type="checkbox"
            checked={this.props.inStockOnly}
            onChange={this.handleInStockChange}
          />
          {' '}
          XXL
          </p>
      </div>
      )
  }
}

class Product extends Component {
  render() {
      let prod = this.props.product;
    return (
      <div>
      <img src={require('/static/data/products/${prod.sku}_1.jpg')}/>
      <p>{prod.title}</p>
      <p>{prod.price}</p>
      <p>{prod.availableSizes}</p>
      </div>
      );
  }
}

class ProductList extends Component {
  render() {
    let prodlist = this.props.products;
    let items = prodlist.map((product) => {
      return(
        <Product product={product}/>
        );
    }
    );
    return (
       <p>{items} </p>
      );
  }
}


class App extends Component {
  render() {
    return (
      <div className="App">
      <SizeFilter/>
      <ProductList products={products}/>
      </div>
    );
  }
}


export default App;
